import { useEffect, useState } from "react";
import AllCardsDetailsView from "./AllCardsDetailsView";
import Pagination from "./Pagination";
import axios from 'axios';
import pageLoader from "../assets/loader.gif";

const PeopleDetails = ({ search }) => {
    const [peopleData, setPeopleData] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState();

    useEffect(() => {
        setLoading(true);

        const fetchData = async () => {
            try {
                const response = await axios.get(`https://swapi.dev/api/people/?page=${currentPage}`);
                const data = response.data;
                setPeopleData(data.results);
                setTotalPages(Math.ceil(data.count / 10)); // Assuming 10 items per page
                setLoading(false);
            } catch (error) {
                setError(error);
                setLoading(false);
            }
        };

        fetchData();
    }, [currentPage]);

    const nextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    const prevPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    const setPage = (page) => {
        setCurrentPage(page);
    };

    return (
        <>
            {loading ? (
                <div>
                    <img className="ml-[500px] mt-[100px]" src={pageLoader} />
                </div>
            ) : (
                <>
                    {peopleData
                        .filter((person) =>
                            person.name.toLowerCase().includes(search.toLowerCase())
                        )
                        .map((person) => (
                            <AllCardsDetailsView
                                key={person.name}
                                name={person.name}
                                birth_year={person.birth_year}
                                species={person.species}
                                homeworld={person.homeworld}
                                vehicles={person.vehicles}
                                starships={person.starship}
                                gender={person.gender}
                            />
                        ))}
                    <div>
                        <Pagination
                            setPageValue={setPage}
                            nextPage={nextPage}
                            prevPage={prevPage}
                            currentPage={currentPage}
                            totalPages={totalPages}
                        />
                    </div>
                </>
            )}
        </>
    );
};

export default PeopleDetails;
